#pragma once#define MAX_VER_STRING		20#define MAX_COUNTRY_TEXT 	34#define COUNTRY_ID_SIZE		  2#define CALLSIGN_SIZE 			16#ifndef RFNETID_MAX_CHARS#define RFNETID_MAX_CHARS	32#endif#define firstS24EventType firstUserEventtypedef struct tagS24Info{	//Firmware Info	Byte	byFirmwareVersion[MAX_VER_STRING];	Byte byCountryCode[COUNTRY_ID_SIZE];	Byte byCountryText[MAX_COUNTRY_TEXT];	Byte byCallsign[CALLSIGN_SIZE];	Byte byValidCallsign;		// ** Adapter and BSS ID's ** 	Byte byPreferredBSSId[S24_BSS_ID_LENGTH];	// Preferred BSSId to use	Byte byMandatoryBSSId[S24_BSS_ID_LENGTH];	// Mandatory BSSId to use	Byte byAccessPointBSSId[S24_BSS_ID_LENGTH];	// Preferred BSSId to use	Byte bySPT1740BSSId[S24_BSS_ID_LENGTH];	// Preferred BSSId to use	Byte szESSID[RFNETID_MAX_CHARS + 1]; 		// Contains the NetID, in string form } S24Info;typedef S24Info *S24InfoPtr;//// All these prefs can be set/get using S24GetPreference() and S24SetPreference().//typedef struct tagS24UserNetworkPrefs{	// ** Misc **	Word 	wRxOptions;							// Transmitter Settings	Word 	wTxOptions;							// Receiver Settings	Word 	wAdapterFlags;						// Adapter Option Flags	// ** Adapter and BSS ID's ** 	Byte		byPreferredBSSId[S24_BSS_ID_LENGTH];	// Preferred BSSId to use	Byte		byMandatoryBSSId[S24_BSS_ID_LENGTH];	// Mandatory BSSId to use	char 	szESSID[RFNETID_MAX_CHARS + 1]; 		// Contains the NetID, in string form 		// ** Power Modes ** //	Word 	wBeaconAlgorithm;				// Beacon Algorithm	Word 	wBeaconMin;					// Minimum beacon listen value	Word 	wBeaconMax;					// Maximum beacon listen value	Word	PowerModeInitial;				// Active / initial power mode	Word	PowerModeStage1;				// Stage 1 power mode	Word wVCCOffTimeout;						// Time to VCC OFF from NAP mode	// ** Network Specific prefs ** 	Boolean	bUseDHCP;							// Shall we use DHCP?	Boolean	bEnableDNS;							// Enable DNS?	DWord	addrStaticIP;						// Static IP of the S24 device	DWord	dwSubnetMask;						// Subnet mask for the S24 device	DWord	addrGateway;						// Gateway used by the S24 device	DWord	addrPrimaryDNS;						// Primary DNS used by the S24 device	DWord	addrSecondaryDNS;					// Secondary DNS used by the S24 device	Char		szHostName[40];						// Host name of the S24 device	Char		szDomainName[40];					// Domain name of the S24 device} S24UserNetworkPrefs;